mapper.update:
update ACCOUNT set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from ACCOUNT where ACCOUNT.DELETED = 0



    AND ACCOUNT.ID = #{entity.id,jdbcType=INTEGER}



      AND ACCOUNT.ID IN

        #{listItem}






    AND ACCOUNT.CRETITY = #{entity.balance,jdbcType=DOUBLE}



      AND ACCOUNT.CRETITY >= #{entity.balanceStart,jdbcType=DOUBLE}


      AND ACCOUNT.CRETITY <= #{entity.balanceEnd,jdbcType=DOUBLE}




    AND CIRCLE.REAL_NAME LIKE "%"#{entity.realName,jdbcType=VARCHAR}"%"



    AND ACCOUNT.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND ACCOUNT.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND ACCOUNT.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND ACCOUNT.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND ACCOUNT.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND ACCOUNT.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND ACCOUNT.ENABLED = #{entity.enabled,jdbcType=INTEGER}



    ORDER BY

      ACCOUNT.${listItem.columnKey}
      ${listItem.order}



    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select

from ACCOUNT where ACCOUNT.DELETED = 0


    AND ACCOUNT.ID = #{entity.id,jdbcType=INTEGER}



      AND ACCOUNT.ID IN

        #{listItem}





    AND ACCOUNT.CRETITY = #{entity.balance,jdbcType=DOUBLE}



      AND ACCOUNT.CRETITY >= #{entity.balanceStart,jdbcType=DOUBLE}


      AND ACCOUNT.CRETITY <= #{entity.balanceEnd,jdbcType=DOUBLE}




    AND CIRCLE.REAL_NAME LIKE "%"#{entity.realName,jdbcType=VARCHAR}"%"



    AND ACCOUNT.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND ACCOUNT.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND ACCOUNT.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND ACCOUNT.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND ACCOUNT.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND ACCOUNT.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND ACCOUNT.ENABLED = #{entity.enabled,jdbcType=INTEGER}


) AS total
