mapper.update:
update ACCOUNT set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
select

from ACCOUNT
WHERE ACCOUNT.DELETED = 1

  AND ACCOUNT.REAL_NAME LIKE "%"#{realName,jdbcType=VARCHAR}"%"


  AND ACCOUNT.ID = #{id,jdbcType=INTEGER}



    AND ACCOUNT.ID >= #{id,jdbcType=INTEGER}


    AND ACCOUNT.ID <= #{id,jdbcType=INTEGER}


    AND ACCOUNT.ID IN

      #{listItem}




  AND ACCOUNT.BALANCE = #{balance,jdbcType=DOUBLE}



    AND ACCOUNT.BALANCE >= #{balance,jdbcType=DOUBLE}


    AND ACCOUNT.BALANCE <= #{balance,jdbcType=DOUBLE}



  AND ACCOUNT.LAST_MODIFY_TIME = #{lastModifyTime,jdbcType=TIMESTAMP}



    AND ACCOUNT.LAST_MODIFY_TIME >= #{lastModifyTime,jdbcType=TIMESTAMP}


    AND ACCOUNT.LAST_MODIFY_TIME <= #{lastModifyTime,jdbcType=TIMESTAMP}



  AND ACCOUNT.CREATE_TIME = #{id,jdbcType=TIMESTAMP}



    AND ACCOUNT.CREATE_TIME >= #{id,jdbcType=TIMESTAMP}


    AND ACCOUNT.CREATE_TIME <= #{id,jdbcType=TIMESTAMP}



  AND ACCOUNT.ENABLED = #{enabled,jdbcType=INTEGER}
