mapper.update:
update ACTIVITY set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from ACTIVITY where ACTIVITY.DELETED = 0



    AND ACTIVITY.ID = #{entity.id,jdbcType=INTEGER}



      AND ACTIVITY.ID IN

        #{listItem}





    AND ACTIVITY.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"



    AND ACTIVITY.PARTNER_ID = #{entity.partnerId,jdbcType=INTEGER}



      AND ACTIVITY.PARTNER_ID IN

        #{listItem}





    AND ACTIVITY.COURSE_ID = #{entity.courseId,jdbcType=INTEGER}



      AND ACTIVITY.COURSE_ID IN

        #{listItem}





    AND ACTIVITY.REMARK LIKE "%"#{entity.remark,jdbcType=VARCHAR}"%"



    AND ACTIVITY.START_TIME =
    #{entity.startTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND ACTIVITY.START_TIME >= #{entity.startTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}


      AND ACTIVITY.START_TIME <= #{entity.startTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}




    AND ACTIVITY.END_TIME =
    #{entity.endTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}



      AND ACTIVITY.END_TIME >= #{entity.endTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}


      AND ACTIVITY.END_TIME <= #{entity.endTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}




    AND ACTIVITY.START = #{entity.status,jdbcType=INTEGER}



      AND ACCOUNT_HISTORY.START IN

        #{listItem}





    AND ACTIVITY.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}



      AND ACTIVITY.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}


      AND ACTIVITY.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}




    AND ACTIVITY.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}



      AND ACTIVITY.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}


      AND ACTIVITY.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}




    AND ACTIVITY.ENABLED = #{entity.enabled,jdbcType=INTEGER}



  ORDER BY

    ACTIVITY.${listItem.columnKey} ${listItem.order}




  LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select

from ACTIVITY where ACTIVITY.DELETED = 0



