mapper.update:
update ACTIVITY set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from ACTIVITY where ACTIVITY.DELETED = 0


    AND ACTIVITY.ID = #{entity.id,jdbcType=INTEGER}



      AND ACTIVITY.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND ACTIVITY.ID <= #{entity.idEnd,jdbcType=INTEGER}


      AND ACTIVITY.ID IN

        #{listItem}





    AND ACTIVITY.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"




      AND ACTIVITY.PARTNER_ID >= #{entity.partnerIdStart,jdbcType=INTEGER}


      AND ACTIVITY.PARTNER_ID <= #{entity.partnerIdEnd,jdbcType=INTEGER}





      AND ACTIVITY.COURSE_ID >= #{entity.courseIdStart,jdbcType=INTEGER}


      AND ACTIVITY.COURSE_ID <= #{entity.courseIdEnd,jdbcType=INTEGER}




    AND ACTIVITY.REMARK LIKE "%"#{entity.remark,jdbcType=VARCHAR}"%"



    AND ACTIVITY.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}



      AND ACTIVITY.START_TIME >= #{entity.startTimeStart,jdbcType=TIMESTAMP}


      AND ACTIVITY.START_TIME <= #{entity.startTimeEnd,jdbcType=TIMESTAMP}




    AND ACTIVITY.END_TIME = #{entity.endTime,jdbcType=TIMESTAMP}



      AND ACTIVITY.END_TIME >= #{entity.endTimeStart,jdbcType=TIMESTAMP}


      AND ACTIVITY.END_TIME <= #{entity.endTimeEnd,jdbcType=TIMESTAMP}





      AND ACTIVITY.START >= #{entity.statusStart,jdbcType=INTEGER}


      AND ACTIVITY.START <= #{entity.statusEnd,jdbcType=INTEGER}




    AND ACTIVITY.LAST_MODIFY_TIME = #{entity.lastModifyTime,jdbcType=TIMESTAMP}



      AND ACTIVITY.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,jdbcType=TIMESTAMP}


      AND ACTIVITY.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,jdbcType=TIMESTAMP}




    AND ACTIVITY.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}



      AND ACTIVITY.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND ACTIVITY.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}




    AND ACTIVITY.ENABLED = #{entity.enabled,jdbcType=INTEGER}



    ORDER BY

      ACTIVITY.${listItem.columnKey} ${listItem.order}



    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select

from ACTIVITY where ACTIVITY.DELETED = 0


    AND ACTIVITY.ID = #{entity.id,jdbcType=INTEGER}



      AND ACTIVITY.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND ACTIVITY.ID <= #{entity.idEnd,jdbcType=INTEGER}


      AND ACTIVITY.ID IN

        #{listItem}





    AND ACTIVITY.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"




      AND ACTIVITY.PARTNER_ID >= #{entity.partnerIdStart,jdbcType=INTEGER}


      AND ACTIVITY.PARTNER_ID <= #{entity.partnerIdEnd,jdbcType=INTEGER}





      AND ACTIVITY.COURSE_ID >= #{entity.courseIdStart,jdbcType=INTEGER}


      AND ACTIVITY.COURSE_ID <= #{entity.courseIdEnd,jdbcType=INTEGER}




    AND ACTIVITY.REMARK LIKE "%"#{entity.remark,jdbcType=VARCHAR}"%"



    AND ACTIVITY.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}



      AND ACTIVITY.START_TIME >= #{entity.startTimeStart,jdbcType=TIMESTAMP}


      AND ACTIVITY.START_TIME <= #{entity.startTimeEnd,jdbcType=TIMESTAMP}




    AND ACTIVITY.END_TIME = #{entity.endTime,jdbcType=TIMESTAMP}



      AND ACTIVITY.END_TIME >= #{entity.endTimeStart,jdbcType=TIMESTAMP}


      AND ACTIVITY.END_TIME <= #{entity.endTimeEnd,jdbcType=TIMESTAMP}





      AND ACTIVITY.START >= #{entity.statusStart,jdbcType=INTEGER}


      AND ACTIVITY.START <= #{entity.statusEnd,jdbcType=INTEGER}




    AND ACTIVITY.LAST_MODIFY_TIME = #{entity.lastModifyTime,jdbcType=TIMESTAMP}



      AND ACTIVITY.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,jdbcType=TIMESTAMP}


      AND ACTIVITY.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,jdbcType=TIMESTAMP}




    AND ACTIVITY.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}



      AND ACTIVITY.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND ACTIVITY.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}




    AND ACTIVITY.ENABLED = #{entity.enabled,jdbcType=INTEGER}


) AS
total
