mapper.update:
update ADDRESS set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from ADDRESS where ADDRESS.DELETED = 0



    AND ADDRESS.ID = #{entity.id,jdbcType=INTEGER}



      AND ADDRESS.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND ADDRESS.ID <= #{entity.idEnd,jdbcType=INTEGER}


      AND ADDRESS.ID IN

        #{listItem}





    AND ADDRESS.PARTNER_ID = #{entity.partnerId,jdbcType=INTEGER}



      AND ADDRESS.PARTNER_ID >= #{entity.partnerIdStart,jdbcType=INTEGER}


      AND ADDRESS.PARTNER_ID <= #{entity.partnerIdEnd,jdbcType=INTEGER}


      AND ADDRESS.PARTNER_ID IN

        #{listItem}





    AND ADDRESS.LOCATION_ID = #{entity.locationId,jdbcType=INTEGER}



      AND ADDRESS.LOCATION_ID >= #{entity.locationIdStart,jdbcType=INTEGER}


      AND ADDRESS.LOCATION_ID <= #{entity.locationIdEnd,jdbcType=INTEGER}


      AND ADDRESS.LOCATION_ID IN

        #{listItem}





    AND ADDRESS.CIRCLE_ID = #{entity.circleId,jdbcType=INTEGER}



      AND ADDRESS.CIRCLE_ID >= #{entity.circleIdStart,jdbcType=INTEGER}


      AND ADDRESS.CIRCLE_ID <= #{entity.circleIdEnd,jdbcType=INTEGER}


      AND ADDRESS.CIRCLE_ID IN

        #{listItem}






    AND ADDRESS.DETAIL LIKE "%"#{entity.detail,jdbcType=VARCHAR}"%"



    AND ADDRESS.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}



      AND ADDRESS.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND ADDRESS.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}



    AND ADDRESS.LAST_MODIFY_TIME = #{entity.lastModifyTime,jdbcType=TIMESTAMP}



      AND ADDRESS.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,jdbcType=TIMESTAMP}


      AND ADDRESS.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,jdbcType=TIMESTAMP}




    AND ADDRESS.ENABLED = #{entity.enabled,jdbcType=INTEGER}




      ORDER BY

        ADDRESS.${listItem.columnKey} ${listItem.order}


    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
 select

from ADDRESS where ADDRESS.DELETED = 0



    AND ADDRESS.ID = #{entity.id,jdbcType=INTEGER}



      AND ADDRESS.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND ADDRESS.ID <= #{entity.idEnd,jdbcType=INTEGER}


      AND ADDRESS.ID IN

        #{listItem}





    AND ADDRESS.PARTNER_ID = #{entity.partnerId,jdbcType=INTEGER}



      AND ADDRESS.PARTNER_ID >= #{entity.partnerIdStart,jdbcType=INTEGER}


      AND ADDRESS.PARTNER_ID <= #{entity.partnerIdEnd,jdbcType=INTEGER}


      AND ADDRESS.PARTNER_ID IN

        #{listItem}





    AND ADDRESS.LOCATION_ID = #{entity.locationId,jdbcType=INTEGER}



      AND ADDRESS.LOCATION_ID >= #{entity.locationIdStart,jdbcType=INTEGER}


      AND ADDRESS.LOCATION_ID <= #{entity.locationIdEnd,jdbcType=INTEGER}


      AND ADDRESS.LOCATION_ID IN

        #{listItem}





    AND ADDRESS.CIRCLE_ID = #{entity.circleId,jdbcType=INTEGER}



      AND ADDRESS.CIRCLE_ID >= #{entity.circleIdStart,jdbcType=INTEGER}


      AND ADDRESS.CIRCLE_ID <= #{entity.circleIdEnd,jdbcType=INTEGER}


      AND ADDRESS.CIRCLE_ID IN

        #{listItem}






    AND ADDRESS.DETAIL LIKE "%"#{entity.detail,jdbcType=VARCHAR}"%"



    AND ADDRESS.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}



      AND ADDRESS.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND ADDRESS.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}



    AND ADDRESS.LAST_MODIFY_TIME = #{entity.lastModifyTime,jdbcType=TIMESTAMP}



      AND ADDRESS.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,jdbcType=TIMESTAMP}


      AND ADDRESS.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,jdbcType=TIMESTAMP}




    AND ADDRESS.ENABLED = #{entity.enabled,jdbcType=INTEGER}


) AS total
