mapper.update:
update APPROVE_HISTORY set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from APPROVE_HISTORY where APPROVE_HISTORY.DELETED = 0


    AND APPROVE_HISTORY.ID = #{entity.id,jdbcType=INTEGER}



      AND APPROVE_HISTORY.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND APPROVE_HISTORY.ID <= #{entity.idEnd,jdbcType=INTEGER}


      AND APPROVE_HISTORY.ID IN

        #{listItem}






      AND APPROVE_HISTORY.TYPE >= #{entity.typeStart,jdbcType=INTEGER}


      AND APPROVE_HISTORY.TYPE <= #{entity.typeEnd,jdbcType=INTEGER}




      AND APPROVE_HISTORY.ATTACHED_ID >= #{entity.attachedIdStart,jdbcType=INTEGER}


      AND APPROVE_HISTORY.ATTACHED_ID <= #{entity.attachedIdEnd,jdbcType=INTEGER}




      AND APPROVE_HISTORY.SUBMIT_USER_ID >= #{entity.submitUserIdStart,jdbcType=INTEGER}


      AND APPROVE_HISTORY.SUBMIT_USER_ID <= #{entity.submitUserIdEnd,jdbcType=INTEGER}





      AND APPROVE_HISTORY.APPROVE_USER_ID >= #{entity.approveUserIdStart,jdbcType=INTEGER}


      AND APPROVE_HISTORY.APPROVE_USER_ID <= #{entity.approveUserIdEnd,jdbcType=INTEGER}





      AND APPROVE_HISTORY.PER_STATUS >= #{entity.preStatusStart,jdbcType=INTEGER}


      AND APPROVE_HISTORY.PER_STATUS <= #{entity.preStatusEnd,jdbcType=INTEGER}





      AND APPROVE_HISTORY.POST_STATUS >= #{entity.postStatusStart,jdbcType=INTEGER}


      AND APPROVE_HISTORY.POST_STATUS <= #{entity.postStatusEnd,jdbcType=INTEGER}




    AND APPROVE_HISTORY.COMMENT LIKE "%"#{entity.comment,jdbcType=VARCHAR}"%"



    AND APPROVE_HISTORY.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}



      AND APPROVE_HISTORY.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND APPROVE_HISTORY.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}




    ORDER BY

      APPROVE_HISTORY.${listItem.columnKey} ${listItem.order}



    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select

from APPROVE_HISTORY where APPROVE_HISTORY.DELETED = 0


    AND APPROVE_HISTORY.ID = #{entity.id,jdbcType=INTEGER}



      AND APPROVE_HISTORY.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND APPROVE_HISTORY.ID <= #{entity.idEnd,jdbcType=INTEGER}


      AND APPROVE_HISTORY.ID IN

        #{listItem}





      AND APPROVE_HISTORY.TYPE >= #{entity.typeStart,jdbcType=INTEGER}


      AND APPROVE_HISTORY.TYPE <= #{entity.typeEnd,jdbcType=INTEGER}




      AND APPROVE_HISTORY.ATTACHED_ID >= #{entity.attachedIdStart,jdbcType=INTEGER}


      AND APPROVE_HISTORY.ATTACHED_ID <= #{entity.attachedIdEnd,jdbcType=INTEGER}




      AND APPROVE_HISTORY.SUBMIT_USER_ID >= #{entity.submitUserIdStart,jdbcType=INTEGER}


      AND APPROVE_HISTORY.SUBMIT_USER_ID <= #{entity.submitUserIdEnd,jdbcType=INTEGER}





      AND APPROVE_HISTORY.APPROVE_USER_ID >= #{entity.approveUserIdStart,jdbcType=INTEGER}


      AND APPROVE_HISTORY.APPROVE_USER_ID <= #{entity.approveUserIdEnd,jdbcType=INTEGER}





      AND APPROVE_HISTORY.PER_STATUS >= #{entity.preStatusStart,jdbcType=INTEGER}


      AND APPROVE_HISTORY.PER_STATUS <= #{entity.preStatusEnd,jdbcType=INTEGER}





      AND APPROVE_HISTORY.POST_STATUS >= #{entity.postStatusStart,jdbcType=INTEGER}


      AND APPROVE_HISTORY.POST_STATUS <= #{entity.postStatusEnd,jdbcType=INTEGER}




    AND APPROVE_HISTORY.COMMENT LIKE "%"#{entity.comment,jdbcType=VARCHAR}"%"



    AND APPROVE_HISTORY.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}



      AND APPROVE_HISTORY.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND APPROVE_HISTORY.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}



  ) AS total
