where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from BOOKING where BOOKING.DELETED = 0


    AND BOOKING.ID = #{entity.id,jdbcType=INTEGER}



      AND BOOKING.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND BOOKING.ID <= #{entity.idEnd,jdbcType=INTEGER}


      AND BOOKING.ID IN

        #{listItem}






      AND BOOKING.USER_ID >= #{entity.userIdStart,jdbcType=INTEGER}


      AND BOOKING.USER_ID <= #{entity.userIdEnd,jdbcType=INTEGER}





      AND BOOKING.COURSE_ID >= #{entity.courseIdStart,jdbcType=INTEGER}


      AND BOOKING.COURSE_ID <= #{entity.courseIdEnd,jdbcType=INTEGER}




    AND PARTNER.COURSE_TEMPLATE_ID = #{entity.courseTemplateId,jdbcType=INTEGER}



      AND PARTNER.COURSE_TEMPLATE_ID >= #{entity.courseTemplateIdStart,jdbcType=INTEGER}


      AND PARTNER.COURSE_TEMPLATE_ID <= #{entity.courseTemplateIdEnd,jdbcType=INTEGER}


      AND PARTNER.COURSE_TEMPLATE_ID IN

        #{listItem}





    AND BOOKING.REFERENCE LIKE "%"#{entity.reference,jdbcType=VARCHAR}"%"


    AND BOOKING.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"


    AND BOOKING.PHON LIKE "%"#{entity.phone,jdbcType=VARCHAR}"%"


    AND BOOKING.EMAIL LIKE "%"#{entity.email,jdbcType=VARCHAR}"%"




      AND BOOKING.STATUS >= #{entity.statusStart,jdbcType=INTEGER}


      AND BOOKING.STATUS <= #{entity.statusEnd,jdbcType=INTEGER}




    AND BOOKING.SCHEDULED_TIME = #{entity.scheduledTime,jdbcType=TIMESTAMP}



      AND BOOKING.SCHEDULED_TIME >= #{entity.scheduledTimeStart,jdbcType=TIMESTAMP}


      AND BOOKING.SCHEDULED_TIME <= #{entity.scheduledTimeEnd,jdbcType=TIMESTAMP}




    AND BOOKING.NO_REFUND_DATE = #{entity.noRefundDate,jdbcType=TIMESTAMP}



      AND BOOKING.NO_REFUND_DATE >= #{entity.noRefundDateStart,jdbcType=TIMESTAMP}


      AND BOOKING.NO_REFUND_DATE <= #{entity.noRefundDateEnd,jdbcType=TIMESTAMP}




    AND BOOKING.CASHBACK_DATE = #{entity.cashbackDate,jdbcType=TIMESTAMP}



      AND BOOKING.CASHBACK_DATE >= #{entity.cashbackDateStart,jdbcType=TIMESTAMP}


      AND BOOKING.CASHBACK_DATE <= #{entity.cashbackDateEnd,jdbcType=TIMESTAMP}




    AND BOOKING.TYPE = #{entity.type,jdbcType=INTEGER}



      AND BOOKING.TYPE >= #{entity.typeStart,jdbcType=INTEGER}


      AND BOOKING.TYPE <= #{entity.typeEnd,jdbcType=INTEGER}




    AND BOOKING.NOTE LIKE "%"#{entity.note,jdbcType=VARCHAR}"%"



    AND BOOKING.CASHBACKAMOUNT = #{entity.cashbackAmount,jdbcType=DOUBLE}



      AND BOOKING.CASHBACKAMOUNT >= #{entity.cashbackAmountStart,jdbcType=DOUBLE}


      AND BOOKING.CASHBACKAMOUNT <= #{entity.cashbackAmountEnd,jdbcType=DOUBLE}




    AND BOOKING.LOST_MODIFY_TIME = #{entity.lastModifyTime,jdbcType=TIMESTAMP}



      AND BOOKING.LOST_MODIFY_TIME >= #{entity.lastModifyTimeStart,jdbcType=TIMESTAMP}


      AND BOOKING.LOST_MODIFY_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}




    AND BOOKING.CREATE_TIME = #{entity.createTilastModifyTimee=TIMESTAMP}



      AND BOOKING.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND BOOKING.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}



    AND BOOKING.ENABLED = #{entity.enabled,jdbcType=INTEGER}


    ORDER BY

      BOOKING.${listItem.columnKey} ${listItem.order}



    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
