mapper.update:
update BOOKING set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from BOOKING where BOOKING.DELETED = 0



    AND BOOKING.ID = #{entity.id,jdbcType=INTEGER}



      AND BOOKING.ID IN

        #{listItem}





    AND BOOKING.USER_ID = #{entity.userId,jdbcType=INTEGER}



      AND BOOKING.USER_ID IN

        #{listItem}





    AND BOOKING.COURSE_ID = #{entity.courseId,jdbcType=INTEGER}



      AND BOOKING.COURSE_ID IN

        #{listItem}





    AND PARTNER.COURSE_TEMPLATE_ID = #{entity.courseTemplateId,jdbcType=INTEGER}



      AND PARTNER.COURSE_TEMPLATE_ID IN

        #{listItem}





    AND BOOKING.REFERENCE LIKE "%"#{entity.reference,jdbcType=VARCHAR}"%"


    AND BOOKING.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"


    AND BOOKING.PHON LIKE "%"#{entity.phone,jdbcType=VARCHAR}"%"


    AND BOOKING.EMAIL LIKE "%"#{entity.email,jdbcType=VARCHAR}"%"



    AND PARTNER.STATUS = #{entity.status,jdbcType=INTEGER}



      AND PARTNER.STATUS IN

        #{listItem}





    AND BOOKING.SCHEDULED_TIME =
    #{entity.scheduledTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}



      AND BOOKING.SCHEDULED_TIME >= #{entity.scheduledTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}


      AND BOOKING.SCHEDULED_TIME <= #{entity.scheduledTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}




    AND BOOKING.NO_REFUND_DATE =
    #{entity.noRefundDate,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}



      AND BOOKING.NO_REFUND_DATE >= #{entity.noRefundDateStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}


      AND BOOKING.NO_REFUND_DATE <= #{entity.noRefundDateEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}




    AND BOOKING.CASHBACK_DATE =
    #{entity.cashbackDate,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}



      AND BOOKING.CASHBACK_DATE >= #{entity.cashbackDateStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}


      AND BOOKING.CASHBACK_DATE <= #{entity.cashbackDateEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}




    AND BOOKING.TYPE = #{entity.type,jdbcType=INTEGER}



      AND PARTNER.TYPE IN

        #{listItem}





    AND BOOKING.NOTE LIKE "%"#{entity.note,jdbcType=VARCHAR}"%"



    AND BOOKING.CASHBACKAMOUNT = #{entity.cashbackAmount,jdbcType=DOUBLE}



      AND BOOKING.CASHBACKAMOUNT >= #{entity.cashbackAmountStart,jdbcType=DOUBLE}


      AND BOOKING.CASHBACKAMOUNT <= #{entity.cashbackAmountEnd,jdbcType=DOUBLE}




    AND BOOKING.LOST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}



      AND BOOKING.LOST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}


      AND BOOKING.LOST_MODIFY_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}




    AND BOOKING.CREATE_TIME = #{entity.createTime,
    typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND BOOKING.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}


      AND BOOKING.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}




    AND BOOKING.ENABLED = #{entity.enabled,jdbcType=INTEGER}



  ORDER BY

    BOOKING.${listItem.columnKey} ${listItem.order}




  LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
