mapper.update:
update CATEGORY set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from CATEGORY where CATEGORY.DELETED = 0



    AND CATEGORY.ID = #{entity.id,jdbcType=INTEGER}




      AND CATEGORY.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND CATEGORY.ID <= #{entity.idEnd,jdbcType=INTEGER}




    AND CATEGORY.RANK = #{entity.rank,jdbcType=INTEGER}




      AND CATEGORY.RANK >= #{entity.rankStart,jdbcType=INTEGER}


      AND CATEGORY.RANK <= #{entity.rankEnd,jdbcType=INTEGER}




  AND CATEGORY.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"


  AND CATEGORY.VALUE LIKE "%"#{entity.value,jdbcType=VARCHAR}"%"



  AND CATEGORY.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}




      AND CATEGORY.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND CATEGORY.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}




    AND CATEGORY.LAST_MODIFY_TIME = #{entity.lastModifyTime,jdbcType=TIMESTAMP}




      AND CATEGORY.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,jdbcType=TIMESTAMP}


      AND CATEGORY.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,jdbcType=TIMESTAMP}




    AND CATEGORY.ENABLED = #{entity.enabled,jdbcType=INTEGER}



    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}
