mapper.update:
update CATEGORY set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from CATEGORY where CATEGORY.DELETED = 0



    AND CATEGORY.ID = #{entity.id,jdbcType=INTEGER}



      AND CATEGORY.ID IN

        #{listItem}





    AND CATEGORY.RANK = #{entity.rank,jdbcType=INTEGER}



      AND CATEGORY.RANK >= #{entity.rankStart,jdbcType=INTEGER}


      AND CATEGORY.RANK <= #{entity.rankEnd,jdbcType=INTEGER}


      AND CATEGORY.RANK IN

        #{listItem}





    AND CATEGORY.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"


    AND CATEGORY.VALUE LIKE "%"#{entity.value,jdbcType=VARCHAR}"%"



    AND CATEGORY.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND CATEGORY.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND CATEGORY.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND CATEGORY.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND CATEGORY.CREATE_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND CATEGORY.CREATE_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND CATEGORY.ENABLED = #{entity.enabled,jdbcType=INTEGER}



  ORDER BY

    CATEGORY.${listItem.columnKey} ${listItem.order}




  LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select

from CATEGORY where CATEGORY.DELETED = 0



    AND CATEGORY.ID = #{entity.id,jdbcType=INTEGER}



      AND CATEGORY.ID IN

        #{listItem}





    AND CATEGORY.RANK = #{entity.rank,jdbcType=INTEGER}



      AND CATEGORY.RANK >= #{entity.rankStart,jdbcType=INTEGER}


      AND CATEGORY.RANK <= #{entity.rankEnd,jdbcType=INTEGER}


      AND CATEGORY.RANK IN

        #{listItem}





    AND CATEGORY.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"


    AND CATEGORY.VALUE LIKE "%"#{entity.value,jdbcType=VARCHAR}"%"



    AND CATEGORY.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND CATEGORY.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND CATEGORY.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND CATEGORY.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND CATEGORY.CREATE_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND CATEGORY.CREATE_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND CATEGORY.ENABLED = #{entity.enabled,jdbcType=INTEGER}


) AS total
