mapper.update:
update CIRCLE set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from CIRCLE where CIRCLE.DELETED = 0



    AND CIRCLE.ID = #{entity.id,jdbcType=INTEGER}




      AND CIRCLE.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND CIRCLE.ID <= #{entity.idEnd,jdbcType=INTEGER}




  AND CIRCLE.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"


  AND CIRCLE.VALUE LIKE "%"#{entity.value,jdbcType=VARCHAR}"%"



      AND CIRCLE.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND CIRCLE.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}



      AND CIRCLE.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,jdbcType=TIMESTAMP}


      AND CIRCLE.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,jdbcType=TIMESTAMP}



    AND CIRCLE.ENABLED = #{entity.enabled,jdbcType=INTEGER}



    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
 select

from CIRCLE where CIRCLE.DELETED = 0



    AND CIRCLE.ID = #{entity.id,jdbcType=INTEGER}




      AND CIRCLE.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND CIRCLE.ID <= #{entity.idEnd,jdbcType=INTEGER}




  AND CIRCLE.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"


  AND CIRCLE.VALUE LIKE "%"#{entity.value,jdbcType=VARCHAR}"%"




      AND CIRCLE.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND CIRCLE.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}



      AND CIRCLE.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,jdbcType=TIMESTAMP}


      AND CIRCLE.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,jdbcType=TIMESTAMP}




    AND CIRCLE.ENABLED = #{entity.enabled,jdbcType=INTEGER}


) AS total
