mapper.update:
update CONTACT set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from CONTACT where CONTACT.DELETED = 0



    AND CONTACT.ID = #{entity.id,jdbcType=INTEGER}



      AND CONTACT.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND CONTACT.ID <= #{entity.idEnd,jdbcType=INTEGER}




    AND CONTACT.USER_ID = #{entity.userId,jdbcType=INTEGER}



      AND CONTACT.USER_ID >= #{entity.userIdStart,jdbcType=INTEGER}


      AND CONTACT.USER_ID <= #{entity.userIdEnd,jdbcType=INTEGER}




    AND CONTACT.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"


    AND CONTACT.PHONE LIKE "%"#{entity.phone,jdbcType=VARCHAR}"%"


    AND CONTACT.EMAIL LIKE "%"#{entity.email,jdbcType=VARCHAR}"%"



    AND CONTACT.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}



      AND CONTACT.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND CONTACT.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}




    AND CONTACT.LAST_MODIFY_TIME = #{entity.lastModifyTime,jdbcType=TIMESTAMP}



      AND CONTACT.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,jdbcType=TIMESTAMP}


      AND CONTACT.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,jdbcType=TIMESTAMP}




    AND CONTACT.ENABLED = #{entity.enabled,jdbcType=INTEGER}



    ORDER BY

      CONTACT.${listItem.columnKey} ${listItem.order}



    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select

from CONTACT where CONTACT.DELETED = 0



    AND CONTACT.ID = #{entity.id,jdbcType=INTEGER}



      AND CONTACT.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND CONTACT.ID <= #{entity.idEnd,jdbcType=INTEGER}




    AND CONTACT.USER_ID = #{entity.userId,jdbcType=INTEGER}



      AND CONTACT.USER_ID >= #{entity.userIdStart,jdbcType=INTEGER}


      AND CONTACT.USER_ID <= #{entity.userIdEnd,jdbcType=INTEGER}




    AND CONTACT.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"


    AND CONTACT.PHONE LIKE "%"#{entity.phone,jdbcType=VARCHAR}"%"


    AND CONTACT.EMAIL LIKE "%"#{entity.email,jdbcType=VARCHAR}"%"



    AND CONTACT.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}



      AND CONTACT.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND CONTACT.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}




    AND CONTACT.LAST_MODIFY_TIME = #{entity.lastModifyTime,jdbcType=TIMESTAMP}



      AND CONTACT.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,jdbcType=TIMESTAMP}


      AND CONTACT.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,jdbcType=TIMESTAMP}




    AND CONTACT.ENABLED = #{entity.enabled,jdbcType=INTEGER}



    ORDER BY

      CONTACT.${listItem.columnKey} ${listItem.order}



    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}


) AS total
