mapper.update:
update COUPON set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from COUPON where COUPON.DELETED = 0



    AND COUPON.ID = #{entity.id,jdbcType=INTEGER}



      AND COUPON.ID IN

        #{listItem}





    AND COUPON.USER_ID = #{entity.userId,jdbcType=INTEGER}



      AND COUPON.USER_ID IN

        #{listItem}





    AND COUPON.TOTAL = #{entity.total,jdbcType=DOUBLE}



      AND COUPON.TOTAL >= #{entity.totalStart,jdbcType=DOUBLE}


      AND COUPON.TOTAL <= #{entity.totalEnd,jdbcType=DOUBLE}




    AND COUPON.BALANCE = #{entity.balance,jdbcType=DOUBLE}




      AND COUPON.BALANCE >= #{entity.balanceStart,jdbcType=DOUBLE}


      AND COUPON.BALANCE <= #{entity.balanceEnd,jdbcType=DOUBLE}




    AND COUPON.BALANCE = #{entity.origin,jdbcType=INTEGER}



      AND COUPON.ORIGIN IN

        #{listItem}





    AND COUPON.CODE LIKE "%"#{entity.code,jdbcType=VARCHAR}"%"


    AND COUPON.REMARK LIKE "%"#{entity.remark,jdbcType=VARCHAR}"%"



    AND COUPON.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND COUPON.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND COUPON.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND COUPON.EXPIRY_TIME =
    #{entity.expiryTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND COUPON.EXPIRY_TIME >= #{entity.expiryTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND COUPON.EXPIRY_TIME <= #{entity.expiryTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND COUPON.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND COUPON.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND COUPON.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND COUPON.ENABLED = #{entity.enabled,jdbcType=INTEGER}



    ORDER BY

      COUPON.${listItem.columnKey} ${listItem.order}




    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select

from COUPON where COUPON.DELETED = 0



    AND COUPON.ID = #{entity.id,jdbcType=INTEGER}



