mapper.update:
update COUPON_HISTORY set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
select

from COUPON_HISTORY
where COUPON_HISTORY.DELETED = 0


    AND COUPON_HISTORY.ID = #{entity.id,jdbcType=INTEGER}



      AND COUPON_HISTORY.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND COUPON_HISTORY.ID <= #{entity.idEnd,jdbcType=INTEGER}


      AND COUPON_HISTORY.ID IN

        #{listItem}




    AND COUPON_HISTORY.COUPON_ID = #{entity.couponId,jdbcType=INTEGER}


    AND COUPON_HISTORY.CHARGE = #{entity.charge,jdbcType=DOUBLE}



      AND COUPON_HISTORY.LAST_MODIFY_TIME >= #{entity.lastModifyTime,jdbcType=TIMESTAMP}


      AND COUPON_HISTORY.LAST_MODIFY_TIME <= #{entity.lastModifyTime,jdbcType=TIMESTAMP}



      AND COUPON_HISTORY.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND COUPON_HISTORY.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}




  ORDER BY

    COUPON_HISTORY.${listItem.columnKey} ${listItem.order}




  LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select

from COUPON_HISTORY
where COUPON_HISTORY.DELETED = 0


    AND COUPON_HISTORY.ID = #{entity.id,jdbcType=INTEGER}



      AND COUPON_HISTORY.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND COUPON_HISTORY.ID <= #{entity.idEnd,jdbcType=INTEGER}


      AND COUPON_HISTORY.ID IN

        #{listItem}




    AND COUPON_HISTORY.COUPON_ID = #{entity.couponId,jdbcType=INTEGER}


    AND COUPON_HISTORY.CHARGE = #{entity.charge,jdbcType=DOUBLE}



      AND COUPON_HISTORY.LAST_MODIFY_TIME >= #{entity.lastModifyTime,jdbcType=TIMESTAMP}


      AND COUPON_HISTORY.LAST_MODIFY_TIME <= #{entity.lastModifyTime,jdbcType=TIMESTAMP}



      AND COUPON_HISTORY.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND COUPON_HISTORY.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}




  ORDER BY

    COUPON_HISTORY.${listItem.columnKey} ${listItem.order}




  LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

) AS total
