mapper.update:
update COURSE_COMMENT set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
select

from COURSE_COMMENT
where COURSE_COMMENT.DELETED = 0



    AND COURSE_COMMENT.ID = #{entity.id,jdbcType=INTEGER}



      AND COURSE_COMMENT.ID IN

        #{listItem}





    AND COURSE_COMMENT.TOTAL_RATING = #{entity.totalRating,jdbcType=DOUBLE}



      AND COURSE_COMMENT.TOTAL_RATING >= #{entity.totalRatingStart,jdbcType=DOUBLE}


      AND COURSE_COMMENT.TOTAL_RATING <= #{entity.totalRatingEnd,jdbcType=DOUBLE}




    AND COURSE_COMMENT.CONDITION_RATING = #{entity.conditionRating,jdbcType=DOUBLE}



      AND COURSE_COMMENT.CONDITION_RATING >= #{entity.conditionRatingStart,jdbcType=DOUBLE}


      AND COURSE_COMMENT.CONDITION_RATING <= #{entity.conditionRatingEnd,jdbcType=DOUBLE}




    AND COURSE_COMMENT.ATTITUDE_RATING = #{entity.attitudeRating,jdbcType=DOUBLE}



      AND COURSE_COMMENT.ATTITUDE_RATING >= #{entity.attitudeRatingStart,jdbcType=DOUBLE}


      AND COURSE_COMMENT.ATTITUDE_RATING <= #{entity.attitudeRatingEnd,jdbcType=DOUBLE}




    AND COURSE_COMMENT.SATISFACTION_RATING =
    #{entity.satisfactionRating,jdbcType=DOUBLE}



      AND COURSE_COMMENT.SATISFACTION_RATING >= #{entity.satisfactionRatingStart,jdbcType=DOUBLE}


      AND COURSE_COMMENT.SATISFACTION_RATING <= #{entity.satisfactionRatingEnd,jdbcType=DOUBLE}




    AND COURSE_COMMENT.USER_ID = #{entity.userId,jdbcType=INTEGER}



      AND COURSE_COMMENT.USER_ID IN

        #{listItem}





    AND COURSE_COMMENT.COURSE_TEMPLATE_ID =
    #{entity.courseTemplateId,jdbcType=INTEGER}



      AND COURSE_COMMENT.COURSE_TEMPLATE_ID IN

        #{listItem}





    AND COURSE_COMMENT.TITLE LIKE "%"#{entity.title,jdbcType=VARCHAR}"%"


    AND COURSE_COMMENT.CONTENT LIKE "%"#{entity.content,jdbcType=VARCHAR}"%"


    AND COURSE_COMMENT.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND COURSE_COMMENT.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND COURSE_COMMENT.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



    AND COURSE_COMMENT.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND COURSE_COMMENT.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND COURSE_COMMENT.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



    AND COURSE_COMMENT.ENABLED = #{entity.enabled,jdbcType=INTEGER}




  ORDER BY

    COURSE_COMMENT.${listItem.columnKey} ${listItem.order}




  LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select

from COURSE_COMMENT
where COURSE_COMMENT.DELETED = 0



    AND COURSE_COMMENT.ID = #{entity.id,jdbcType=INTEGER}



      AND COURSE_COMMENT.ID IN

        #{listItem}





    AND COURSE_COMMENT.TOTAL_RATING = #{entity.totalRating,jdbcType=DOUBLE}



      AND COURSE_COMMENT.TOTAL_RATING >= #{entity.totalRatingStart,jdbcType=DOUBLE}


      AND COURSE_COMMENT.TOTAL_RATING <= #{entity.totalRatingEnd,jdbcType=DOUBLE}




    AND COURSE_COMMENT.CONDITION_RATING = #{entity.conditionRating,jdbcType=DOUBLE}



      AND COURSE_COMMENT.CONDITION_RATING >= #{entity.conditionRatingStart,jdbcType=DOUBLE}


      AND COURSE_COMMENT.CONDITION_RATING <= #{entity.conditionRatingEnd,jdbcType=DOUBLE}




    AND COURSE_COMMENT.ATTITUDE_RATING = #{entity.attitudeRating,jdbcType=DOUBLE}



      AND COURSE_COMMENT.ATTITUDE_RATING >= #{entity.attitudeRatingStart,jdbcType=DOUBLE}


      AND COURSE_COMMENT.ATTITUDE_RATING <= #{entity.attitudeRatingEnd,jdbcType=DOUBLE}




    AND COURSE_COMMENT.SATISFACTION_RATING =
    #{entity.satisfactionRating,jdbcType=DOUBLE}



      AND COURSE_COMMENT.SATISFACTION_RATING >= #{entity.satisfactionRatingStart,jdbcType=DOUBLE}


      AND COURSE_COMMENT.SATISFACTION_RATING <= #{entity.satisfactionRatingEnd,jdbcType=DOUBLE}




    AND COURSE_COMMENT.USER_ID = #{entity.userId,jdbcType=INTEGER}



      AND COURSE_COMMENT.USER_ID IN

        #{listItem}





    AND COURSE_COMMENT.COURSE_TEMPLATEID = #{entity.courseTemplateId,jdbcType=INTEGER}



      AND COURSE_COMMENT.COURSE_TEMPLATEID IN

        #{listItem}





    AND COURSE_COMMENT.TITLE LIKE "%"#{entity.title,jdbcType=VARCHAR}"%"


    AND COURSE_COMMENT.CONTENT LIKE "%"#{entity.content,jdbcType=VARCHAR}"%"


    AND COURSE_COMMENT.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND COURSE_COMMENT.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND COURSE_COMMENT.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



    AND COURSE_COMMENT.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND COURSE_COMMENT.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND COURSE_COMMENT.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



    AND COURSE_COMMENT.ENABLED = #{entity.enabled,jdbcType=INTEGER}


) AS total
