,
from COURSE COURSE
join COURSE_TEACHER COURSE_TEACHER ON COURSE.ID = COURSE_TEACHER.COURSE_ID
join TEACEHR TEACHER ON TEACHER.ID = COURSE_TEACHER.TEACHER_ID
join COURSE_CLASS_PHOTO COURSE_CLASS_PHOTO ON COURSE.ID = COURSE_CLASS_PHOTO.COURSE_ID
join CLASS_PHOTO CLASS_PHOTO ON CLASS_PHOTO.ID = COURSE_CLASS_PHOTO.CLASS_PHOTO_ID
join PARTNER PARTNER ON PARTNER.ID = COURSE.PARTNER_ID
join CIRCLE CIRCLE ON CIRCLE.ID = COURSE.CIRCLE_ID
join ADDRESS ADDRESS ON ADDRESS.ID = COURSE.ADDRESS_ID
join CATEGORY CATEGORY ON CATEGORY.ID = ADDRESS.CATEGORY_ID
join LOCATION LOCATION ON LOCATION.ID = ADDRESS.LOCATION_ID

WHERE COURSE.DELETED = 0


    AND COURSE.COURSENAME LIKE "%"#{entity.courseName,jdbcType=VARCHAR}"%"


    AND COURSE.ID = #{entity.id,jdbcType=INTEGER}



      AND COURSE.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND COURSE.ID <= #{entity.idEnd,jdbcType=INTEGER}


      AND COURSE.ID IN

        #{listItem}




    AND COURSE.LAST_MODIFY_TIME = #{entity.lastModifyTime,jdbcType=TIMESTAMP}



      AND COURSE.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,jdbcType=TIMESTAMP}


      AND COURSE.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,jdbcType=TIMESTAMP}



    AND COURSE.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}



      AND COURSE.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND COURSE.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}



    AND COURSE.ENABLED = #{entity.enabled,jdbcType=INTEGER}


