mapper.select:
select

from COURSE_PROMOTION where COURSE_PROMOTION.DELETED = 0


    AND COURSE_PROMOTION.ID = #{entity.id,jdbcType=INTEGER}



    AND COURSE_PROMOTION.COURSE_ID = #{entity.courseId,jdbcType=INTEGER}



    AND COURSE_PROMOTION.CATEGORY_ID = #{entity.categoryId,jdbcType=INTEGER}



    AND COURSE_PROMOTION.STATUS = #{entity.status,jdbcType=INTEGER}



    AND COURSE_PROMOTION.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND COURSE_PROMOTION.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND COURSE_PROMOTION.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND COURSE_PROMOTION.START_TIME =
    #{entity.startTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND COURSE_PROMOTION.START_TIME >= #{entity.startTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND COURSE_PROMOTION.START_TIME <= #{entity.startTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND COURSE_PROMOTION.END_TIME =
    #{entity.endTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND COURSE_PROMOTION.END_TIME >= #{entity.endTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND COURSE_PROMOTION.END_TIME <= #{entity.endTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}






  ORDER BY

    COURSE_PROMOTION.${listItem.columnKey} ${listItem.order}




  LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

select count(*) from (
select

from COURSE_PROMOTION where COURSE_PROMOTION.DELETED = 0


    AND COURSE_PROMOTION.ID = #{entity.id,jdbcType=INTEGER}



