mapper.update:
update COURSE_TEACHER set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
select

from COURSE_TEACHER
where COURSE_TEACHER.DELETED = 0


    AND COURSE_TEACHER.ID = #{entity.id,jdbcType=INTEGER}



      AND COURSE_TEACHER.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND COURSE_TEACHER.ID <= #{entity.idEnd,jdbcType=INTEGER}


      AND COURSE_TEACHER.ID IN

        #{listItem}




    AND COURSE_TEACHER.COURSE_ID = #{entity.courseId,jdbcType=INTEGER}


    AND COURSE_TEACHER.TEACHERID = #{entity.teacherId,jdbcType=INTEGER}



    AND COURSE_TEACHER.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}



      AND COURSE_TEACHER.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND COURSE_TEACHER.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}




  ORDER BY

    COURSE_TEACHER.${listItem.columnKey} ${listItem.order}




  LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select

from COURSE_TEACHER
where COURSE_TEACHER.DELETED = 0


    AND COURSE_TEACHER.ID = #{entity.id,jdbcType=INTEGER}



      AND COURSE_TEACHER.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND COURSE_TEACHER.ID <= #{entity.idEnd,jdbcType=INTEGER}


      AND COURSE_TEACHER.ID IN

        #{listItem}




    AND COURSE_TEACHER.COURSE_ID = #{entity.courseId,jdbcType=INTEGER}


    AND COURSE_TEACHER.TEACHERID = #{entity.teacherId,jdbcType=INTEGER}



    AND COURSE_TEACHER.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}



      AND COURSE_TEACHER.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND COURSE_TEACHER.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}



) AS total
