select

from COURSE_TEMPLATE_TEACHER
where COURSE_TEMPLATE_TEACHER.DELETED = 0


    AND COURSE_TEMPLATE_TEACHER.ID = #{entity.id,jdbcType=INTEGER}



      AND COURSE_TEMPLATE_TEACHER.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND COURSE_TEMPLATE_TEACHER.ID <= #{entity.idEnd,jdbcType=INTEGER}


      AND COURSE_TEMPLATE_TEACHER.ID IN

        #{listItem}




    AND COURSE_TEMPLATE_TEACHER.COURSE_TEMPLATE_ID =
    #{entity.courseTemplateId,jdbcType=INTEGER}



      AND COURSE_TEMPLATE_TEACHER.COURSE_TEMPLATE_ID IN

        #{listItem}




    AND COURSE_TEMPLATE_TEACHER.TEACHERID = #{entity.teacherId,jdbcType=INTEGER}



      AND COURSE_TEMPLATE_TEACHER.TEACHERID IN

        #{listItem}





    AND COURSE_TEMPLATE_TEACHER.CREATE_TIME = #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND COURSE_TEMPLATE_TEACHER.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND COURSE_TEMPLATE_TEACHER.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




  ORDER BY

    COURSE_TEMPLATE_TEACHER.${listItem.columnKey} ${listItem.order}




  LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select

from COURSE_TEMPLATE_TEACHER
