mapper.update:
update CREDIT set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from CREDIT where CREDIT.DELETED = 0


    AND CREDIT.ID = #{entity.id,jdbcType=INTEGER}



      AND CREDIT.ID IN

        #{listItem}





    AND CREDIT.CREDIT = #{entity.credit,jdbcType=DOUBLE}



      AND CREDIT.CREDIT >= #{entity.creditStart,jdbcType=DOUBLE}


      AND CREDIT.CREDIT <= #{entity.creditEnd,jdbcType=DOUBLE}




    AND CREDIT.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND CREDIT.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND CREDIT.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND CREDIT.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND CREDIT.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND CREDIT.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND CREDIT.ENABLED = #{entity.enabled,jdbcType=INTEGER}



    ORDER BY

      CREDIT.${listItem.columnKey}
      ${listItem.order}



    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select

from CREDIT where CREDIT.DELETED = 0


    AND CREDIT.ID = #{entity.id,jdbcType=INTEGER}



      AND CREDIT.ID IN

        #{listItem}





    AND CREDIT.CRETITY = #{entity.credit,jdbcType=DOUBLE}



      AND CREDIT.CRETITY >= #{entity.creditStart,jdbcType=DOUBLE}


      AND CREDIT.CRETITY <= #{entity.creditEnd,jdbcType=DOUBLE}




    AND CREDIT.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND CREDIT.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND CREDIT.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND CREDIT.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND CREDIT.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND CREDIT.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND CREDIT.ENABLED = #{entity.enabled,jdbcType=INTEGER}


) AS total
