#{id,jdbcType=INTEGER}

SELECT

from CREDIT_HISTORY where CREDIT_HISTORY.DELETED = 0



    AND CREDIT_HISTORY.ID = #{entity.id,jdbcType=INTEGER}



      AND CREDIT_HISTORY.ID IN

        #{listItem}





    AND CREDIT_HISTORY.USER_ID = #{entity.userId,jdbcType=INTEGER}



      AND CREDIT_HISTORY.USER_ID IN

        #{listItem}





    AND CREDIT_HISTORY.CHARGE = #{entity.charge,jdbcType=DOUBLE}



      AND CREDIT_HISTORY.CHARGE >= #{entity.chargeStart,jdbcType=DOUBLE}


      AND CREDIT_HISTORY.CHARGE <= #{entity.chargeEnd,jdbcType=DOUBLE}




    AND CREDIT_HISTORY.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND CREDIT_HISTORY.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND CREDIT_HISTORY.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND CREDIT_HISTORY.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}



      AND CREDIT_HISTORY.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}


      AND CREDIT_HISTORY.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}}




  ORDER BY

    CREDIT_HISTORY.${listItem.columnKey} ${listItem.order}




  LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select

from CREDIT_HISTORY where CREDIT_HISTORY.DELETED = 0



    AND CREDIT_HISTORY.ID = #{entity.id,jdbcType=INTEGER}



