mapper.update:
update CREDIT_HISTORY set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from CREDIT_HISTORY where CREDIT_HISTORY.DELETED = 0



    AND CREDIT_HISTORY.ID = #{entity.id,jdbcType=INTEGER}




      AND CREDIT_HISTORY.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND CREDIT_HISTORY.ID <= #{entity.idEnd,jdbcType=INTEGER}




    AND CREDIT_HISTORY.USER_ID = #{entity.userId,jdbcType=INTEGER}




      AND CREDIT_HISTORY.USER_ID >= #{entity.userIdStart,jdbcType=INTEGER}


      AND CREDIT_HISTORY.USER_ID <= #{entity.userIdEnd,jdbcType=INTEGER}




    AND CREDIT_HISTORY.CHARGE = #{entity.charge,jdbcType=DOUBLE}




      AND CREDIT_HISTORY.CHARGE >= #{entity.chargeStart,jdbcType=DOUBLE}


      AND CREDIT_HISTORY.CHARGE <= #{entity.chargeEnd,jdbcType=DOUBLE}




      AND CREDIT_HISTORY.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND CREDIT_HISTORY.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}




      AND CREDIT_HISTORY.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,jdbcType=TIMESTAMP}


      AND CREDIT_HISTORY.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,jdbcType=TIMESTAMP}




    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
 select

from CREDIT_HISTORY where CREDIT_HISTORY.DELETED = 0



    AND CREDIT_HISTORY.ID = #{entity.id,jdbcType=INTEGER}




      AND CREDIT_HISTORY.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND CREDIT_HISTORY.ID <= #{entity.idEnd,jdbcType=INTEGER}




    AND CREDIT_HISTORY.USER_ID = #{entity.userId,jdbcType=INTEGER}




      AND CREDIT_HISTORY.USER_ID >= #{entity.userIdStart,jdbcType=INTEGER}


      AND CREDIT_HISTORY.USER_ID <= #{entity.userIdEnd,jdbcType=INTEGER}




    AND CREDIT_HISTORY.CHARGE = #{entity.charge,jdbcType=DOUBLE}




      AND CREDIT_HISTORY.CHARGE >= #{entity.chargeStart,jdbcType=DOUBLE}


      AND CREDIT_HISTORY.CHARGE <= #{entity.chargeEnd,jdbcType=DOUBLE}




      AND CREDIT_HISTORY.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND CREDIT_HISTORY.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}



      AND CREDIT_HISTORY.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,jdbcType=TIMESTAMP}


      AND CREDIT_HISTORY.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,jdbcType=TIMESTAMP}



    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}



) AS total
