mapper.select:
select

,

,

from GROUP_BUY_ACTIVITY
left join COURSE on COURSE.ID = GROUP_BUY_ACTIVITY.COURSE_ID
left join GROUP_BUY_PHOTO on GROUP_BUY_PHOTO.GROUP_BUY_ACTIVITY_ID = GROUP_BUY_ACTIVITY.ID and GROUP_BUY_PHOTO.DELETED = 0
where GROUP_BUY_ACTIVITY.DELETED = 0


        AND GROUP_BUY_ACTIVITY.ID = #{entity.id,jdbcType=INTEGER}



            AND GROUP_BUY_ACTIVITY.ID IN

                #{listItem}





        AND GROUP_BUY_ACTIVITY.COURSE_ID = #{entity.courseId,jdbcType=INTEGER}



            AND GROUP_BUY_ACTIVITY.COURSE_ID IN

                #{listItem}





        AND GROUP_BUY_ACTIVITY.END_TIME = #{entity.endTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



            AND GROUP_BUY_ACTIVITY.END_TIME >= #{entity.endTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


            AND GROUP_BUY_ACTIVITY.END_TIME <= #{entity.endTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




        AND GROUP_BUY_ACTIVITY.STATUS = #{entity.status,jdbcType=INTEGER}



            AND GROUP_BUY_ACTIVITY.STATUS IN

                #{listItem}





        AND GROUP_BUY_ACTIVITY.CREATE_TIME = #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



            AND GROUP_BUY_ACTIVITY.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


            AND GROUP_BUY_ACTIVITY.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




        AND GROUP_BUY_ACTIVITY.GROUP_BUY_PRICE = #{entity.groupBuyPrice,jdbcType=DOUBLE}



            AND GROUP_BUY_ACTIVITY.GROUP_BUY_PRICE >= #{entity.groupBuyPriceStart,jdbcType=DOUBLE}


            AND GROUP_BUY_ACTIVITY.GROUP_BUY_PRICE <= #{entity.groupBuyPriceEnd,jdbcType=DOUBLE}





    ORDER BY 

        GROUP_BUY_ACTIVITY.${listItem.columnKey} ${listItem.order}




    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

select count(*) from (
select

,

from GROUP_BUY_ACTIVITY
left join COURSE on COURSE.ID = GROUP_BUY_ACTIVITY.COURSE_ID
where GROUP_BUY_ACTIVITY.DELETED = 0


        AND GROUP_BUY_ACTIVITY.ID = #{entity.id,jdbcType=INTEGER}



            AND GROUP_BUY_ACTIVITY.ID IN

                #{listItem}





        AND GROUP_BUY_ACTIVITY.COURSE_ID = #{entity.courseId,jdbcType=INTEGER}



            AND GROUP_BUY_ACTIVITY.COURSE_ID IN

                #{listItem}





        AND GROUP_BUY_ACTIVITY.END_TIME = #{entity.endTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



            AND GROUP_BUY_ACTIVITY.END_TIME >= #{entity.endTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


            AND GROUP_BUY_ACTIVITY.END_TIME <= #{entity.endTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




        AND GROUP_BUY_ACTIVITY.STATUS = #{entity.status,jdbcType=INTEGER}



            AND GROUP_BUY_ACTIVITY.STATUS IN

                #{listItem}





        AND GROUP_BUY_ACTIVITY.CREATE_TIME = #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



            AND GROUP_BUY_ACTIVITY.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


            AND GROUP_BUY_ACTIVITY.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




        AND GROUP_BUY_ACTIVITY.GROUP_BUY_PRICE = #{entity.groupBuyPrice,jdbcType=DOUBLE}



            AND GROUP_BUY_ACTIVITY.GROUP_BUY_PRICE >= #{entity.groupBuyPriceStart,jdbcType=DOUBLE}


            AND GROUP_BUY_ACTIVITY.GROUP_BUY_PRICE <= #{entity.groupBuyPriceEnd,jdbcType=DOUBLE}



) as total
