mapper.update:
update GROUP_BUY_ACTIVITY
set DELETED = 1 where ID =
#{id,jdbcType=INTEGER}

mapper.select:
select

,

,

,

,

from GROUP_BUY_ACTIVITY
left join COURSE_TEMPLATE on COURSE_TEMPLATE.ID =
GROUP_BUY_ACTIVITY.COURSE_ID
left join
(select course_template_id as course_template_id,avg(CONDITION_RATING) as avgcondition,avg(ATTITUDE_RATING) as
avgattitude,avg(SATISFACTION_RATING) as avgsatisfaction from course_comment group by course_template_id) as temp on
temp.course_template_id =
GROUP_BUY_ACTIVITY.COURSE_ID
left join GROUP_BUY_PHOTO on
GROUP_BUY_PHOTO.GROUP_BUY_ACTIVITY_ID = GROUP_BUY_ACTIVITY.ID and
GROUP_BUY_PHOTO.DELETED =
0
left join GROUP_BUY_ADDRESS on
GROUP_BUY_ACTIVITY.ID = GROUP_BUY_ADDRESS.GROUP_BUY_ACTIVITY_ID and
GROUP_BUY_ADDRESS.DELETED = 0
left join ADDRESS on
GROUP_BUY_ADDRESS.ADDRESS_ID = ADDRESS.ID and ADDRESS.DELETED = 0
where GROUP_BUY_ACTIVITY.DELETED = 0


    AND GROUP_BUY_ACTIVITY.ID = #{entity.id,jdbcType=INTEGER}



      AND GROUP_BUY_ACTIVITY.ID IN

        #{listItem}





    AND GROUP_BUY_ACTIVITY.COURSE_ID = #{entity.courseId,jdbcType=INTEGER}



      AND GROUP_BUY_ACTIVITY.COURSE_ID IN

        #{listItem}





    AND GROUP_BUY_ACTIVITY.END_TIME =
    #{entity.endTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



            AND GROUP_BUY_ACTIVITY.END_TIME >= #{entity.endTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


