mapper.select:
select

,

,

from GROUP_BUY_BOOKING
left join GROUP_BUY_ACTIVITY on GROUP_BUY_ACTIVITY.ID = GROUP_BUY_BOOKING.GROUP_BUY_ACTIVITY_ID
left join USERS on USERS.ID = GROUP_BUY_BOOKING.USER_ID
where GROUP_BUY_BOOKING.DELETED = 0


        AND GROUP_BUY_BOOKING.ID = #{entity.id,jdbcType=INTEGER}



            AND GROUP_BUY_BOOKING.ID IN

                #{listItem}





        AND GROUP_BUY_BOOKING.GROUP_BUY_PRICE = #{entity.groupBuyPrice,jdbcType=DOUBLE}



            AND GROUP_BUY_BOOKING.GROUP_BUY_PRICE >= #{entity.groupBuyPriceStart,jdbcType=DOUBLE}


            AND GROUP_BUY_BOOKING.GROUP_BUY_PRICE <= #{entity.groupBuyPriceEnd,jdbcType=DOUBLE}




        AND GROUP_BUY_BOOKING.GROUP_BUY_ACTIVITY_ID = #{entity.groupBuyActivityId,jdbcType=INTEGER}



            AND GROUP_BUY_BOOKING.GROUP_BUY_ACTIVITY_ID IN

                #{listItem}





        AND GROUP_BUY_BOOKING.USER_ID = #{entity.userId,jdbcType=INTEGER}



            AND GROUP_BUY_BOOKING.USER_ID IN

                #{listItem}





        AND GROUP_BUY_BOOKING.STATUS = #{entity.status,jdbcType=INTEGER}



            AND GROUP_BUY_BOOKING.STATUS IN

                #{listItem}





        AND GROUP_BUY_BOOKING.CREATE_TIME = #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



            AND GROUP_BUY_BOOKING.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


            AND GROUP_BUY_BOOKING.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




        AND GROUP_BUY_BOOKING.REFERENCE = #{entity.reference,jdbcType=VARCHAR}



        AND GROUP_BUY_BOOKING.NUMBER = #{entity.number,jdbcType=VARCHAR}




    ORDER BY 

        GROUP_BUY_BOOKING.${listItem.columnKey} ${listItem.order}




    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

select count(*) from (
select

,

,

from GROUP_BUY_BOOKING
left join GROUP_BUY_ACTIVITY on GROUP_BUY_ACTIVITY.ID = GROUP_BUY_BOOKING.GROUP_BUY_ACTIVITY_ID
left join USERS on USERS.ID = GROUP_BUY_BOOKING.USER_ID
where GROUP_BUY_BOOKING.DELETED = 0


        AND GROUP_BUY_BOOKING.ID = #{entity.id,jdbcType=INTEGER}



            AND GROUP_BUY_BOOKING.ID IN

                #{listItem}





        AND GROUP_BUY_BOOKING.GROUP_BUY_PRICE = #{entity.groupBuyPrice,jdbcType=DOUBLE}



            AND GROUP_BUY_BOOKING.GROUP_BUY_PRICE >= #{entity.groupBuyPriceStart,jdbcType=DOUBLE}


            AND GROUP_BUY_BOOKING.GROUP_BUY_PRICE <= #{entity.groupBuyPriceEnd,jdbcType=DOUBLE}




        AND GROUP_BUY_BOOKING.GROUP_BUY_ACTIVITY_ID = #{entity.groupBuyActivityId,jdbcType=INTEGER}



            AND GROUP_BUY_BOOKING.GROUP_BUY_ACTIVITY_ID IN

                #{listItem}





        AND GROUP_BUY_BOOKING.USER_ID = #{entity.userId,jdbcType=INTEGER}



            AND GROUP_BUY_BOOKING.USER_ID IN

                #{listItem}





        AND GROUP_BUY_BOOKING.STATUS = #{entity.status,jdbcType=INTEGER}



            AND GROUP_BUY_BOOKING.STATUS IN

                #{listItem}





        AND GROUP_BUY_BOOKING.CREATE_TIME = #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



            AND GROUP_BUY_BOOKING.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


