mapper.update:
update GROUPS set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from GROUPS where GROUPS.DELETED = 0



    AND GROUPS.ID = #{entity.id,jdbcType=INTEGER}




      AND GROUPS.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND GROUPS.ID <= #{entity.idEnd,jdbcType=INTEGER}




    AND GROUPS.ROLE_ID = #{entity.roleId,jdbcType=INTEGER}




      AND GROUPS.ROLE_ID >= #{entity.roleIdStart,jdbcType=INTEGER}


      AND GROUPS.ROLE_ID <= #{entity.roleIdEnd,jdbcType=INTEGER}




    AND GROUPS.PARTNER_ID = #{entity.roleId,jdbcType=INTEGER}




      AND GROUPS.PARTNER_ID >= #{entity.partnerIdStart,jdbcType=INTEGER}


      AND GROUPS.PARTNER_ID <= #{entity.partnerIdEnd,jdbcType=INTEGER}




  AND GROUPS.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"



  AND GROUPS.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}




      AND GROUPS.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND GROUPS.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}




    AND GROUPS.LAST_MODIFY_TIME = #{entity.lastModifyTime,jdbcType=TIMESTAMP}




      AND GROUPS.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,jdbcType=TIMESTAMP}


      AND GROUPS.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,jdbcType=TIMESTAMP}




    AND GROUPS.ENABLED = #{entity.enabled,jdbcType=INTEGER}



    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}
