mapper.update:
update LOCATION set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from LOCATION where LOCATION.DELETED = 0



    AND LOCATION.ID = #{entity.id,jdbcType=INTEGER}




      AND LOCATION.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND LOCATION.ID <= #{entity.idEnd,jdbcType=INTEGER}




  AND LOCATION.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"


  AND LOCATION.VALUE LIKE "%"#{entity.value,jdbcType=VARCHAR}"%"



  AND LOCATION.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}




      AND LOCATION.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND LOCATION.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}




    AND LOCATION.LAST_MODIFY_TIME = #{entity.lastModifyTime,jdbcType=TIMESTAMP}




      AND LOCATION.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,jdbcType=TIMESTAMP}


      AND LOCATION.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,jdbcType=TIMESTAMP}




    AND LOCATION.ENABLED = #{entity.enabled,jdbcType=INTEGER}



    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}
