mapper.update:
update LOCATION set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from LOCATION where LOCATION.DELETED = 0



    AND LOCATION.ID = #{entity.id,jdbcType=INTEGER}




      AND LOCATION.ID IN

        #{listItem}





    AND LOCATION.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"


    AND LOCATION.VALUE LIKE "%"#{entity.value,jdbcType=VARCHAR}"%"



    AND LOCATION.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




      AND LOCATION.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND LOCATION.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND LOCATION.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




      AND LOCATION.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND LOCATION.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND LOCATION.ENABLED = #{entity.enabled,jdbcType=INTEGER}




  ORDER BY

    LOCATION.${listItem.columnKey} ${listItem.order}




  LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select

from LOCATION where LOCATION.DELETED = 0



    AND LOCATION.ID = #{entity.id,jdbcType=INTEGER}




      AND LOCATION.ID IN

        #{listItem}





    AND LOCATION.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"


    AND LOCATION.VALUE LIKE "%"#{entity.value,jdbcType=VARCHAR}"%"



    AND LOCATION.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




      AND LOCATION.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND LOCATION.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND LOCATION.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




      AND LOCATION.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND LOCATION.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND LOCATION.ENABLED = #{entity.enabled,jdbcType=INTEGER}


) AS total
