mapper.update:
update MESSAGE set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from MESSAGE where MESSAGE.DELETED = 0



    AND MESSAGE.ID = #{entity.id,jdbcType=INTEGER}




      AND MESSAGE.ID IN

        #{listItem}





    AND MESSAGE.MESSAGE_TEXT LIKE "%"#{entity.messageText,jdbcType=VARCHAR}"%"



    AND MESSAGE.USER_FROM_ID = #{entity.userFromId,jdbcType=INTEGER}



      AND MESSAGE.USER_FROM_ID IN

        #{listItem}





    AND MESSAGE.USER_TO_ID = #{entity.userToId,jdbcType=INTEGER}



      MESSAGE.USER_TO_ID IN

        #{listItem}





    AND MESSAGE.STATUS = #{entity.status,jdbcType=INTEGER}



      MESSAGE.STATUS IN

        #{listItem}





    AND MESSAGE.READ_TIME =
    #{entity.readTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



        AND MESSAGE.READ_TIME >= #{entity.readTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


        AND MESSAGE.READ_TIME <= #{entity.readTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND MESSAGE.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



        AND MESSAGE.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


        AND MESSAGE.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}





  ORDER BY

    MESSAGE.${listItem.columnKey}
    ${listItem.order}



  LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
SELECT

from MESSAGE where MESSAGE.DELETED = 0



    AND MESSAGE.ID = #{entity.id,jdbcType=INTEGER}




      AND MESSAGE.ID IN

        #{listItem}





    AND MESSAGE.MESSAGE_TEXT LIKE "%"#{entity.messageText,jdbcType=VARCHAR}"%"



    AND MESSAGE.USER_FROM_ID = #{entity.userFromId,jdbcType=INTEGER}



      AND MESSAGE.USER_FROM_ID IN

        #{listItem}





    AND MESSAGE.USER_TO_ID = #{entity.userToId,jdbcType=INTEGER}



      MESSAGE.USER_TO_ID IN

        #{listItem}





    AND MESSAGE.STATUS = #{entity.status,jdbcType=INTEGER}



      MESSAGE.STATUS IN

        #{listItem}





    AND MESSAGE.READ_TIME =
    #{entity.readTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



        AND MESSAGE.READ_TIME >= #{entity.readTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


        AND MESSAGE.READ_TIME <= #{entity.readTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND MESSAGE.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



        AND MESSAGE.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


        AND MESSAGE.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



) AS total
