mapper.update:
update PERMISSIONS set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
select

from PERMISSIONS
where PERMISSIONS.DELETED = 0



    AND PERMISSIONS.ID = #{entity.id,jdbcType=INTEGER}



      AND PERMISSIONS.ID IN

        #{listItem}





    AND PERMISSIONS.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"



    AND PERMISSIONS.PATH LIKE "%"#{entity.path,jdbcType=VARCHAR}"%"



    AND PERMISSIONS.TAG LIKE "%"#{entity.tag,jdbcType=VARCHAR}"%"



    AND PERMISSIONS.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND PERMISSIONS.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND PERMISSIONS.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND PERMISSIONS.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND PERMISSIONS.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND PERMISSIONS.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND PERMISSIONS.ENABLED = #{entity.enabled,jdbcType=INTEGER}




  ORDER BY

    PERMISSIONS.${listItem.columnKey} ${listItem.order}




  LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select

from PERMISSIONS
where PERMISSIONS.DELETED = 0



    AND PERMISSIONS.ID = #{entity.id,jdbcType=INTEGER}



      AND PERMISSIONS.ID IN

        #{listItem}





    AND PERMISSIONS.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"



    AND PERMISSIONS.PATH LIKE "%"#{entity.path,jdbcType=VARCHAR}"%"



    AND PERMISSIONS.TAG LIKE "%"#{entity.tag,jdbcType=VARCHAR}"%"



    AND PERMISSIONS.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND PERMISSIONS.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND PERMISSIONS.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND PERMISSIONS.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND PERMISSIONS.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND PERMISSIONS.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND PERMISSIONS.ENABLED = #{entity.enabled,jdbcType=INTEGER}



) AS total
