mapper.update:
update PERMISSIONS set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
select

from PERMISSIONS
where PERMISSIONS.DELETED = 0


    AND PERMISSIONS.ID = #{entity.id,jdbcType=INTEGER}



      AND PERMISSIONS.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND PERMISSIONS.ID <= #{entity.idEnd,jdbcType=INTEGER}


      AND PERMISSIONS.ID IN

        #{listItem}




    AND PERMISSIONS.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"


    AND PERMISSIONS.PATH LIKE "%"#{entity.path,jdbcType=VARCHAR}"%"





      AND PERMISSIONS.LAST_MODIFY_TIME >= #{entity.lastModifyTime,jdbcType=TIMESTAMP}


      AND PERMISSIONS.LAST_MODIFY_TIME <= #{entity.lastModifyTime,jdbcType=TIMESTAMP}



      AND PERMISSIONS.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND PERMISSIONS.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}



    AND PERMISSIONS.ENABLED = #{entity.enabled,jdbcType=INTEGER}




  ORDER BY

    PERMISSIONS.${listItem.columnKey} ${listItem.order}




  LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

Select count(*) From (
select

  from PERMISSIONS
  where PERMISSIONS.DELETED = 0


      AND PERMISSIONS.ID = #{entity.id,jdbcType=INTEGER}



        AND PERMISSIONS.ID >= #{entity.idStart,jdbcType=INTEGER}


        AND PERMISSIONS.ID <= #{entity.idEnd,jdbcType=INTEGER}


        AND PERMISSIONS.ID IN

          #{listItem}




      AND PERMISSIONS.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"


      AND PERMISSIONS.PATH LIKE "%"#{entity.path,jdbcType=VARCHAR}"%"





        AND PERMISSIONS.LAST_MODIFY_TIME >= #{entity.lastModifyTime,jdbcType=TIMESTAMP}


        AND PERMISSIONS.LAST_MODIFY_TIME <= #{entity.lastModifyTime,jdbcType=TIMESTAMP}



        AND PERMISSIONS.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


        AND PERMISSIONS.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}



      AND PERMISSIONS.ENABLED = #{entity.enabled,jdbcType=INTEGER}




    ORDER BY

      PERMISSIONS.${listItem.columnKey} ${listItem.order}




    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

  ) AS total
