mapper.update:
update ROLES set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from ROLES where ROLES.DELETED = 0



    AND ROLES.ID = #{entity.id,jdbcType=INTEGER}




      AND ROLES.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND ROLES.ID <= #{entity.idEnd,jdbcType=INTEGER}




  AND ROLES.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"



      AND ROLES.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND ROLES.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}




      AND ROLES.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,jdbcType=TIMESTAMP}


      AND ROLES.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,jdbcType=TIMESTAMP}




    AND ROLES.ENABLED = #{entity.enabled,jdbcType=INTEGER}


  ORDER BY

    ROLES.${listItem.columnKey} ${listItem.order}



    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
 select

from ROLES where ROLES.DELETED = 0



    AND ROLES.ID = #{entity.id,jdbcType=INTEGER}




      AND ROLES.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND ROLES.ID <= #{entity.idEnd,jdbcType=INTEGER}




  AND ROLES.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"



      AND ROLES.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND ROLES.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}



      AND ROLES.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,jdbcType=TIMESTAMP}


      AND ROLES.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,jdbcType=TIMESTAMP}




    AND ROLES.ENABLED = #{entity.enabled,jdbcType=INTEGER}


) AS total
