mapper.update:
update ROLES set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from ROLES where ROLES.DELETED = 0


    AND ROLES.ID = #{entity.id,jdbcType=INTEGER}



      AND ROLES.ID IN

        #{listItem}





    AND ROLES.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"



    AND ROLES.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND ROLES.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND ROLES.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND ROLES.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND ROLES.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND ROLES.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND ROLES.ENABLED = #{entity.enabled,jdbcType=INTEGER}




  ORDER BY

    ROLES.${listItem.columnKey} ${listItem.order}




  LIMIT
  #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select

from ROLES where ROLES.DELETED = 0



    AND ROLES.ID = #{entity.id,jdbcType=INTEGER}



      AND ROLES.ID IN

        #{listItem}





    AND ROLES.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"



    AND ROLES.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND ROLES.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND ROLES.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND ROLES.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND ROLES.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND ROLES.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND ROLES.ENABLED = #{entity.enabled,jdbcType=INTEGER}


) AS total
