mapper.update:
update ROLE_PERMISSION set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from ROLE_PERMISSION where ROLE_PERMISSION.DELETED = 0



    AND ROLE_PERMISSION.ID = #{entity.id,jdbcType=INTEGER}




      AND ROLE_PERMISSION.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND ROLE_PERMISSION.ID <= #{entity.idEnd,jdbcType=INTEGER}




    AND ROLE_PERMISSION.ROLE_ID = #{entity.roleId,jdbcType=INTEGER}




      AND ROLE_PERMISSION.ROLE_ID >= #{entity.roleIdStart,jdbcType=INTEGER}


      AND ROLE_PERMISSION.ROLE_ID <= #{entity.roleIdEnd,jdbcType=INTEGER}




    AND ROLE_PERMISSION.PERMISSION_ID = #{entity.permissionId,jdbcType=INTEGER}




      AND ROLE_PERMISSION.PERMISSION_ID >= #{entity.permissionIdStart,jdbcType=INTEGER}


      AND ROLE_PERMISSION.PERMISSION_ID <= #{entity.permissionIdEnd,jdbcType=INTEGER}




    AND ROLE_PERMISSION.LAST_MODIFY_TIME = #{entity.lastModifyTime,jdbcType=TIMESTAMP}




      AND ROLE_PERMISSION.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,jdbcType=TIMESTAMP}


      AND ROLE_PERMISSION.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,jdbcType=TIMESTAMP}




    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}
