mapper.update:
update SCHOOL set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
SELECT

from SCHOOL where SCHOOL.DELETED = 0



    AND SCHOOL.ID = #{entity.id,jdbcType=INTEGER}



      AND SCHOOL.ID IN

        #{listItem}





    AND SCHOOL.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"


    AND SCHOOL.VALUE LIKE "%"#{entity.value,jdbcType=VARCHAR}"%"



    AND SCHOOL.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND SCHOOL.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND SCHOOL.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



    AND SCHOOL.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND SCHOOL.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND SCHOOL.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



    AND SCHOOL.ENABLED = #{entity.enabled,jdbcType=INTEGER}



  ORDER BY

    SCHOOL.${listItem.columnKey} ${listItem.order}




  LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select


from SCHOOL where SCHOOL.DELETED = 0



    AND SCHOOL.ID = #{entity.id,jdbcType=INTEGER}



      AND SCHOOL.ID IN

        #{listItem}





    AND SCHOOL.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"


    AND SCHOOL.VALUE LIKE "%"#{entity.value,jdbcType=VARCHAR}"%"



    AND SCHOOL.CREATE_TIME =
    #{entity.createTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND SCHOOL.CREATE_TIME >= #{entity.createTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND SCHOOL.CREATE_TIME <= #{entity.createTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



    AND SCHOOL.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND SCHOOL.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND SCHOOL.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



    AND SCHOOL.ENABLED = #{entity.enabled,jdbcType=INTEGER}



    ORDER BY

      SCHOOL.${listItem.columnKey} ${listItem.order}




    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}


) AS total
