mapper.update:
update USERS set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
select

from USERS 
where USERS.DELETED = 0


    AND USERS.ID = #{entity.id,jdbcType=INTEGER}



      AND USERS.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND USERS.ID <= #{entity.idEnd,jdbcType=INTEGER}


      AND USERS.ID IN

        #{listItem}





    AND USERS.NAME LIKE "%"#{entity.name,jdbcType=VARCHAR}"%"



    AND USERS.PASSWORD LIKE "%"#{entity.password,jdbcType=VARCHAR}"%"



    AND USERS.PHONE LIKE "%"#{entity.phone,jdbcType=VARCHAR}"%"



    AND USERS.ENABLED = #{entity.enabled,jdbcType=INTEGER}



    AND USERS.WECHAT LIKE "%"#{entity.wechat,jdbcType=VARCHAR}"%"



    AND USERS.EMAIL LIKE "%"#{entity.email,jdbcType=VARCHAR}"%"



    AND USERS.INVITATION_CODE LIKE "%"#{entity.invitationCode,jdbcType=VARCHAR}"%"



    AND USERS.APPLIED_INVITATION_CODE LIKE "%"#{entity.appliedInvitationCode,jdbcType=VARCHAR}"%"



    AND USERS.REFERENCE LIKE "%"#{entity.reference,jdbcType=VARCHAR}"%"



    AND USERS.SCHOOL LIKE "%"#{entity.school,jdbcType=VARCHAR}"%"



    AND USERS.MAJOR LIKE "%"#{entity.major,jdbcType=VARCHAR}"%"



    AND USERS.ENABLED = #{entity.enabled,jdbcType=INTEGER}



    AND USERS.LAST_MODIFY_TIME = #{entity.lastModifyTime,jdbcType=TIMESTAMP}



      AND USERS.LAST_MODIFY_TIME >= #{entity.lastModifyTime,jdbcType=TIMESTAMP}


      AND USERS.LAST_MODIFY_TIME <= #{entity.lastModifyTime,jdbcType=TIMESTAMP}




    AND USERS.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}



      AND USERS.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND USERS.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}




    AND USERS.LAST_LOGIN_TIME = #{entity.lastLoginTime,jdbcType=TIMESTAMP}



      AND USERS.LAST_LOGIN_TIME >= #{entity.lastLoginTime,jdbcType=TIMESTAMP}


      AND USERS.LAST_LOGIN_TIME <= #{entity.lastLoginTime,jdbcType=TIMESTAMP}




    AND USERS.BIRTHDAY = #{entity.birthday,jdbcType=TIMESTAMP}



      AND USERS.BIRTHDAY >= #{entity.birthday,jdbcType=TIMESTAMP}


      AND USERS.BIRTHDAY <= #{entity.birthday,jdbcType=TIMESTAMP}




    LIMIT #{offset,jdbcType=INTEGER},#{size,jdbcType=INTEGER}
