from USER_LOCATION where USER_LOCATION.DELETED = 0 and USER_LOCATION.ID =
#{id,jdbcType=INTEGER}

select

from USER_LOCATION
where USER_LOCATION.DELETED = 0


    AND USER_LOCATION.ID = #{entity.id,jdbcType=INTEGER}



      AND USER_LOCATION.ID IN

        #{listItem}





    AND USER_LOCATION.USER_ID = #{entity.userId,jdbcType=INTEGER}



      AND USER_LOCATION.USER_ID IN

        #{listItem}





    AND USER_LOCATION.LOCATION_ID = #{entity.locationId,jdbcType=INTEGER}



      AND USER_LOCATION.LOCATION_ID IN

        #{listItem}





    AND USER_LOCATION.LAST_MODIFY_TIME =
    #{entity.lastModifyTime,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}



      AND USER_LOCATION.LAST_MODIFY_TIME >= #{entity.lastModifyTimeStart,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}


      AND USER_LOCATION.LAST_MODIFY_TIME <= #{entity.lastModifyTimeEnd,typeHandler=com.ishangke.edunav.dataaccess.typeHandler.CalendarTypeHandler}




    AND USER_LOCATION.ENABLED = #{entity.enabled,jdbcType=INTEGER}



  ORDER BY

    USER_LOCATION.${listItem.columnKey} ${listItem.order}




  LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select

from USER_LOCATION
where USER_LOCATION.DELETED = 0


    AND USER_LOCATION.ID = #{entity.id,jdbcType=INTEGER}



      AND USER_LOCATION.ID IN

        #{listItem}





