mapper.update:
update WITHDRAW set DELETED = 1
where ID =
#{id,jdbcType=INTEGER}

mapper.select:
select

from WITHDRAW WITHDRAW
where WITHDRAW.DELETED = 0


    AND WITHDRAW.ID = #{entity.id,jdbcType=INTEGER}



      AND WITHDRAW.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND WITHDRAW.ID <= #{entity.idEnd,jdbcType=INTEGER}


      AND WITHDRAW.ID IN

        #{listItem}




    AND WITHDRAW.USER_ID = #{entity.userId,jdbcType=INTEGER}


    AND WITHDRAW.PEYEE_NAME LIKE "%"#{entity.peyeeName,jdbcType=VARCHAR}"%"



    AND WITHDRAW.LAST_MODIFY_TIME = #{entity.lastModifyTime,jdbcType=TIMESTAMP}



      AND WITHDRAW.LAST_MODIFY_TIME >= #{entity.lastModifyTime,jdbcType=TIMESTAMP}


      AND WITHDRAW.LAST_MODIFY_TIME <= #{entity.lastModifyTime,jdbcType=TIMESTAMP}




    AND WITHDRAW.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}



      AND WITHDRAW.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND WITHDRAW.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}




    AND WITHDRAW.ENABLED = #{entity.enabled,jdbcType=INTEGER}


    ORDER BY

      WITHDRAW.${listItem.columnKey} ${listItem.order}



    LIMIT #{page.offset,jdbcType=INTEGER},#{page.size,jdbcType=INTEGER}

SELECT COUNT(*) FROM (
select

from WITHDRAW WITHDRAW
where WITHDRAW.DELETED = 0


    AND WITHDRAW.ID = #{entity.id,jdbcType=INTEGER}



      AND WITHDRAW.ID >= #{entity.idStart,jdbcType=INTEGER}


      AND WITHDRAW.ID <= #{entity.idEnd,jdbcType=INTEGER}


      AND WITHDRAW.ID IN

        #{listItem}




    AND WITHDRAW.USER_ID = #{entity.userId,jdbcType=INTEGER}


    AND WITHDRAW.PEYEE_NAME LIKE "%"#{entity.peyeeName,jdbcType=VARCHAR}"%"



    AND WITHDRAW.LAST_MODIFY_TIME = #{entity.lastModifyTime,jdbcType=TIMESTAMP}



      AND WITHDRAW.LAST_MODIFY_TIME >= #{entity.lastModifyTime,jdbcType=TIMESTAMP}


      AND WITHDRAW.LAST_MODIFY_TIME <= #{entity.lastModifyTime,jdbcType=TIMESTAMP}




    AND WITHDRAW.CREATE_TIME = #{entity.createTime,jdbcType=TIMESTAMP}



      AND WITHDRAW.CREATE_TIME >= #{entity.createTimeStart,jdbcType=TIMESTAMP}


      AND WITHDRAW.CREATE_TIME <= #{entity.createTimeEnd,jdbcType=TIMESTAMP}




    AND WITHDRAW.ENABLED = #{entity.enabled,jdbcType=INTEGER}


  ) AS
total
